module number_control(
input [10:0] xx,
input [10:0] yy,
input [31:0] vgadata,
output reg color
    );
    
    reg [3:0] nndd;
    wire [10:0] num=(xx-144)/80;

    
    reg [1599:0] color_data [0:15];
    initial
        color_data[0]=1600';
    initial   
        color_data[1]=1600';
    initial
        color_data[3]=1600';    
    initial
        color_data[2]=1600';
    initial
        color_data[4]=1600';
    initial
        color_data[5]=1600';    
    initial
        color_data[6]=1600';
    initial
        color_data[7]=1600';
    initial
        color_data[8]=1600';
     initial
        color_data[9]=1600';        
     initial
        color_data[10]=1600';       
     initial
        color_data[11]=1600';
     initial        
        color_data[12]=1600';
     initial        
        color_data[13]=1600';
     initial        
        color_data[14]=1600';
     initial        
        color_data[15]=1600';             
        
        
          
    always @(xx or yy)
    begin
     case(num)
           10'd0:nndd<={vgadata[31:28]};
           10'd1:nndd<={vgadata[27:24]};
           10'd2:nndd<={vgadata[23:20]};
           10'd3:nndd<={vgadata[19:16]};
           10'd4:nndd<={vgadata[15:12]};
           10'd5:nndd<={vgadata[11:8]};
           10'd6:nndd<={vgadata[7:4]};
           10'd7:nndd<={vgadata[3:0]};
           default: nndd<={vgadata[31:28]};
           endcase
     if(yy>2+33+200&&yy<2+33+240)
        color<=~color_data[nndd][(6400-((yy-35)%80*80+(xx-144)%80))/2];
     else
        color<=0;
    end
    
endmodule
